
// MODULES //

var // Expectation library:
	chai = require( 'chai' ),

	// Stream spec:
	spec = require( 'stream-spec' ),

	// Test utilities:
	utils = require( './utils' ),

	// Module to be tested:
	mStream = require( './../lib/stats/min' );


// VARIABLES //

var expect = chai.expect,
	assert = chai.assert;


// TESTS //

describe( 'stats/max', function tests() {
	'use strict';

	it( 'should export a factory function', function test() {
		expect( mStream ).to.be.a( 'function' );
	});

	it( 'should provide a method to get the initial accumulator value', function test() {
		var rStream = mStream();
		assert.strictEqual( rStream.value(), Number.POSITIVE_INFINITY );
	});

	it( 'should provide a method to set the initial accumulator value', function test() {
		var rStream = mStream();
		rStream.value( 5 );
		assert.strictEqual( rStream.value(), 5 );
	});

	it( 'should return the minimum value of piped data', function test() {
		var numData = 1000,
			expected = new Array( numData ),
			rStream, s;

		// Simulate some data...
		for ( var i = 0; i < numData; i++ ) {
			expected[ i ] = Math.random() * 1000;
		}

		// Create a new min stream:
		rStream = mStream().stream();

		// Create the stream spec:
		s = spec( rStream )
			.through();

		// Mock reading from the stream:
		utils.readStream( rStream, onRead );

		// Validate the stream when the stream closes:
		rStream.on( 'close', s.validate );

		// Mock piping a data to the stream:
		utils.writeStream( expected, rStream );

		return;

		/**
		* FUNCTION: onRead( error, actual )
		*	Read event handler. Checks for errors and compares streamed data to expected data.
		*/
		function onRead( error, actual ) {
			var min = Number.POSITIVE_INFINITY,
				value;
			for ( var i = 0; i < expected.length; i++ ) {
				value = expected[ i ];
				if ( value < min ) {
					min = value;
				}
			}
			expect( error ).to.not.exist;
			assert.deepEqual( actual[ 0 ], min );
		} // end FUNCTION onRead()
	});

	it( 'should find the min using an arbitrary starting value', function test() {
		var numData = 1000,
			expected = new Array( numData ),
			reducer, rStream,
			initValue = -1;

		// Simulate some data...
		for ( var i = 0; i < numData; i++ ) {
			// No value should ever exceed the initial min value ( expected[ i ] exists on the interval: [0,1] ):
			expected[ i ] = Math.random();
		}

		// Create a new min stream generator:
		reducer = mStream();

		// Set the initial min and create a new stream:
		rStream = reducer.value( initValue )
			.stream();

		// Mock reading from the stream:
		utils.readStream( rStream, onRead );

		// Mock piping a data to the stream:
		utils.writeStream( expected, rStream );

		return;

		/**
		* FUNCTION: onRead( error, actual )
		*	Read event handler. Checks for errors and compares streamed data to expected data.
		*/
		function onRead( error, actual ) {
			expect( error ).to.not.exist;
			assert.deepEqual( actual[ 0 ], initValue );
		} // end FUNCTION onRead()
	});

});